sequential.groovy:

targetName = properties["target.name"]

if ((properties["target.name"] == "jar") ||
	(properties["target.name"] == "javadoc") ||
	(properties["target.name"] == "war")) {

	macrodefName = properties["target.name"] + "-macro"
}
else {
	macrodefName = properties["target.name"]
}

moduleDirs = "${properties["module.dirs"]}".split(",")

for (moduleDir in moduleDirs) {
	ant."set-module-properties"("module.dir": moduleDir)

	buildXmlContent = new File(moduleDir + "/build.xml").text

	if (buildXmlContent =~ "<target name=\"" + targetName + "\"") {
		ant.ant(dir: moduleDir, target: targetName)
	}
	else {
		ant."${macrodefName}"("module.dir": moduleDir)
	}
}

sequential.script:

import com.liferay.util.xml.XMLFormatter;

import org.dom4j.Document;
import org.dom4j.Element;
import org.dom4j.io.SAXReader;

import java.io.File;

SAXReader saxReader = new SAXReader();

Document document = saxReader.read(new File(project.getProperty("web.xml.dir"), "web.xml"));

Element rootElement = document.getRootElement();

Element displayNameElement = rootElement.element("display-name");

if (displayNameElement != null) {
	displayNameElement.detach();
}

List filterElements = rootElement.elements("filter");

for (Element filterElement : filterElements) {
	Element filterNameElement = filterElement.element("filter-name");

	if (filterNameElement == null) {
		continue;
	}

	String filterNameText = filterNameElement.getText();

	if (filterNameText.contains("Invoker Filter -")) {
		filterElement.detach();
	}
}

List filterMappingElements = rootElement.elements("filter-mapping");

for (Element filterMappingElement : filterMappingElements) {
	Element filterNameElement = filterMappingElement.element("filter-name");

	if (filterNameElement == null) {
		continue;
	}

	String filterNameText = filterNameElement.getText();

	if (filterNameText.contains("Invoker Filter -")) {
		filterMappingElement.detach();
	}
}

List listenerElements = rootElement.elements("listener");

for (Element listenerElement : listenerElements) {
	Element listenerClassElement = listenerElement.element("listener-class");

	String listenerClassText = listenerClassElement.getText();

	if (listenerClassText.equals("com.liferay.portal.kernel.servlet.PluginContextListener") ||
		listenerClassText.equals("com.liferay.portal.kernel.servlet.SerializableSessionAttributeListener")) {

		listenerElement.detach();
	}
}

List servletElements = rootElement.elements("servlet");

for (Element servletElement : servletElements) {
	Element servletNameElement = servletElement.element("servlet-name");

	if (servletNameElement == null) {
		continue;
	}

	String servletNameText = servletNameElement.getText();

	if (servletNameText.equals("Dynamic Resource Servlet")) {
		Element servletClassElement = servletElement.element("servlet-class");

		servletClassElement.setText("com.liferay.portal.webserver.DynamicResourceServlet");

		List initParamElements = servletElement.elements("init-param");

		for (Element initParamElement : initParamElements) {
			initParamElement.detach();
		}
	}
}

project.setProperty("web.xml.content", XMLFormatter.toString(document));
