scripted-assignment.script:
							import com.liferay.portal.service.GroupLocalServiceUtil;
							import com.liferay.portal.model.Group;
							import com.liferay.portal.kernel.workflow.WorkflowConstants;
							import com.liferay.portal.service.RoleLocalServiceUtil;
							import com.liferay.portal.model.Role;

							Long companyId = Long.parseLong((String)workflowContext.get(WorkflowConstants.CONTEXT_COMPANY_ID));

							Long groupId = Long.parseLong((String)workflowContext.get(WorkflowConstants.CONTEXT_GROUP_ID));
							Group group = GroupLocalServiceUtil.getGroup(groupId);

							roles = new ArrayList();

							Role adminRole = RoleLocalServiceUtil.getRole(companyId, "Administrator");
							roles.add(adminRole);

							if (group.organization) {
								Role role = RoleLocalServiceUtil.getRole(companyId, "Organization Content Reviewer");
								roles.add(role);
							}
							else {
								Role role = RoleLocalServiceUtil.getRole(companyId, "Site Content Reviewer");
								roles.add(role);
							}

							user = null;
